const createSalesPolicyDOM = (data) => {
    return `
        <div class="row mb-7 fv-row">
            <label class="control-label fs-6 fw-bold mb-2">Tiêu đề</label> 
            <input name="Title" class="form-control form-control-solid mb-3 mb-lg-0" placeholder="Tiêu đề"/>
        </div>
        <div class="row mb-7 fv-row">
            <label class="control-label fs-6 fw-bold mb-2">Tiêu đề phụ</label> 
            <input name="SubTitle" class="form-control form-control-solid mb-3 mb-lg-0" placeholder="Tiêu đề phụ"/>
        </div>
        <div class="row mb-7">
            <div class="col-xl-6 fv-row">
                <label class="control-label fs-6 fw-bold mb-2">Ngày bắt đầu</label> 
                <input name="FromDate" class="form-control form-control-solid mb-3 mb-lg-0" data-date-time="flatpickr"  type="text" /> 
            </div>
            <div class="col-xl-6 fv-row fv-row">
                <label class="control-label fs-6 fw-bold mb-2">Ngày kết thúc</label> 
                <input name="ToDate" class="form-control form-control-solid mb-3 mb-lg-0" data-date-time="flatpickr" type="text" /> 
            </div>
        </div>
        <div class="row mb-7">
            <div class="col-xl-4 form-group fv-row">
                <label class="control-label mb-4">Đã nhập/điều chỉnh thông tin lên Web</label>
                <div class="form-check form-switch form-check-custom form-check-solid">
                    <input name="CompleteEnteringInformation" class="form-check-input" value="true" type="checkbox" />
                    <label class="form-check-label"></label>
                </div>
            </div>
            <div class="col-xl-4 form-group fv-row">
                <label class="control-label mb-4">Đã chuẩn bị chiến dịch Marketing</label>
                <div class="form-check form-switch form-check-custom form-check-solid">
                    <input name="PreparedMarketingCampaign" class="form-check-input" value="true" type="checkbox" />
                    <label class="form-check-label"></label>
                </div>
            </div>
            <div class="col-xl-4 form-group fv-row">
                <label class="control-label mb-4">Đã setup Ads hoàn tất</label>
                <div class="form-check form-switch form-check-custom form-check-solid">
                    <input name="CompleteSetupAds" class="form-check-input" value="true" type="checkbox" />
                    <label class="form-check-label"></label>
                </div>
            </div>
        </div>
        <div class="row mb-7">
            <div class="col-xl-12 form-group fv-row">
                <label class="control-label fs-6 fw-bold mb-2">Mô tả</label>
                <textarea name="Description" class="form-control tinymce"></textarea>
            </div>
        </div>
        <div class="mb-10 form-action">
            <div class="text-center pt-15">
                <button type="reset" class="btn btn-light me-3 remove-form" data-bs-dismiss="modal">Hủy</button>
                <button type="submit" class="btn btn-primary" id="submit-form-create-sale-policy">Lưu</button>
            </div>
        </div>
    `
}

const editSalesPolicyDOM = (data) => {
    return `
    <input name="Id" type="hidden" value="${data.id||''}">
            <div class="row mb-7 fv-row">
                <label class="control-label fs-6 fw-bold mb-2 text-start">Tiêu đề</label> 
                <input name="Title" value="${data.title||''}" class="form-control form-control-solid mb-3 mb-lg-0" placeholder="Tiêu đề"/>
            </div>
            <div class="row mb-7 fv-row">
                <label class="control-label fs-6 fw-bold mb-2 text-start">Tiêu đề phụ</label> 
                <input name="SubTitle" value="${data.subTitle||''}" class="form-control form-control-solid mb-3 mb-lg-0" placeholder="Tiêu đề phụ"/>
            </div>
            <div class="row mb-7 fv-row">
                <label class="w-100 control-label fs-6 fw-bold mb-2 text-start">Trạng thái</label> 
                <select name="Status" class="form-select form-select-solid text-start" data-control="select2"> 
                    <option ${data.status === 0 ?'selected':''} class="text-start" value="0">Đề xuất</option> 
                    <option ${data.status === 1 ?'selected':''} class="text-start" value="1">Hủy</option> 
                    <option ${data.status === 2 ?'selected':''} class="text-start" value="2">Đã duyệt</option> 
                </select> 
            </div>
            <div class="row mb-7 fv-row">
                <div class="col-xl-6">
                    <label class="w-100 control-label fs-6 fw-bold mb-2 text-start">Ngày bắt đầu</label> 
                    <input name="FromDate" value="${data.fromDate.slice(0, -9)||''}" class="form-control form-control-solid mb-3 mb-lg-0" data-date-time="flatpickr"  type="text" /> 
                </div>
                <div class="col-xl-6 fv-row">
                    <label class="w-100 control-label fs-6 fw-bold mb-2 text-start">Ngày kết thúc</label> 
                    <input name="ToDate" value="${data.toDate.slice(0, -9)||''}" class="form-control form-control-solid mb-3 mb-lg-0" data-date-time="flatpickr" type="text" /> 
                </div>
            </div>
            <div class="row mb-7">
                <div class="col-xl-4 form-group fv-row">
                    <label class="w-100 control-label mb-4 text-start">Đã nhập/điều chỉnh thông tin lên Web</label>
                    <div class="form-check form-switch form-check-custom form-check-solid">
                        <input name="CompleteEnteringInformation" ${data.completeEnteringInformation ?'checked':''} class="form-check-input" value="true" type="checkbox" />
                        <label class="form-check-label"></label>
                    </div>
                </div>
                <div class="col-xl-4 form-group fv-row">
                    <label class="w-100 control-label mb-4 text-start">Đã chuẩn bị chiến dịch Marketing</label>
                    <div class="form-check form-switch form-check-custom form-check-solid">
                        <input name="PreparedMarketingCampaign" ${data.preparedMarketingCampaign?'checked':''} class="form-check-input" value="true" type="checkbox" />
                        <label class="form-check-label"></label>
                    </div>
                </div>
                <div class="col-xl-4 form-group fv-row">
                    <label class="w-100 control-label mb-4 text-start">Đã setup Ads hoàn tất</label>
                    <div class="form-check form-switch form-check-custom form-check-solid">
                        <input name="CompleteSetupAds" ${data.completeSetupAds?'checked':''} class="form-check-input" value="true" type="checkbox" />
                        <label class="form-check-label"></label>
                    </div>
                </div>
            </div>
            <div class="row mb-7">
                <div class="col-xl-12 form-group fv-row">
                    <label class="w-100 control-label fs-6 fw-bold mb-2 text-start">Mô tả</label>
                    <textarea name="Description" class="form-control tinymce">${data.description||''}</textarea>
                </div>
            </div>
            <div class="mb-10 form-action">
                <div class="text-center pt-15">
                    <button type="reset" class="btn btn-light me-3 remove-form" data-bs-dismiss="modal">Hủy</button>
                    <button type="submit" class="btn btn-primary" id="submit-form-edit-sale-policy_${data.id}">Lưu</button>
                </div>
            </div>`
}