//TrainingCategory - Edit
const editTrainingCategoryDOM = (item, data ) => {
    console.log("item", item);
    let select = ''; 

    if (item.parentCategoryId) {
        select = `
        <div class="row form-group mb-7">
            <div class="col-xl-6 form-group fv-row">
                <label class="control-label fs-6 fw-bold mb-2">Danh mục cha</label>
                <select name="ParentCategoryId" class="form-select form-select-solid" data-control="training-create-select2">
                    ${ buildOptions(data.listcategory, 'id', 'title', item.parentCategoryId) }
                </select> 
            </div>
        </div>

        `;
    }
    return `
    <div asp-validation-summary="ModelOnly" class="text-danger"></div>
    <input type="hidden" name="ID" value="${ item.id }" />
    <input type="hidden" name="DateCreated" value="${ item.dateCreated }" />
    <input type="hidden" name="IsDelete" value="${ item.isDelete }" />
    <input type="hidden" name="CreatedBy" value="${ item.createdBy }" />
    <div class="row mb-7 form-group fv-row">
        <div class="col-xl-12 ">
            <label class="control-label fs-6 fw-bold mb-2">Tên danh mục đào tạo</label>
            <input name="Title" class="form-control form-control-solid mb-3 mb-lg-0"  autoComplete="off" value="${ item.title }" placeholder="Tên danh mục đào tạo" />
        </div>
     </div>   
     <div class="row form-group mb-7">
         <div class="col-xl-6">
             <label class="control-label mb-4">Ẩn/Hiện</label>
             <div class="form-check form-switch form-check-custom form-check-solid">
                 <input ${ item.status ? "checked" : ""} name="Status" value="true" class="form-check-input" type="checkbox" />
                 <label class="form-check-label"></label>
             </div>
         </div>
     </div>
        ${select}
     <div class="row form-group mb-7 fv-row">
              <label for="">Miêu tả ngắn</label>
              <span class="text-danger"></span>
              <textarea name="Description" class="form-control">${ item.description ?? ""  }</textarea>
     </div>



    </div>

    `;
};



