//User - Create
const createUserDOM = (data) => {
    const genders = [];
    for (const key in data.genders) {
        genders.push({ id: key, name: data.genders[key] });
    };
    return `
        <div class="row mb-7">
            <!--begin::Col-->
            <div class="col-xl-6 form-group fv-row">
                <label class="control-label fs-6 fw-bold mb-2">Họ & tên đệm</label>
                <input name="FirstMidName" class="form-control form-control-solid mb-3 mb-lg-0" autocomplete="off"/>
            </div>
            <!--end::Col-->
            <!--begin::Col-->
            <div class="col-xl-6 form-group fv-row">
                <label class="control-label fs-6 fw-bold mb-2">Tên</label>
                <input name="LastName" class="form-control form-control-solid mb-3 mb-lg-0" autocomplete="off"/>
            </div>
            <!--end::Col-->
        </div>
        <div class="row mb-7">
            <!--begin::Col-->
            <div class="col-xl-6 form-group fv-row">
                <label class="control-label fs-6 fw-bold mb-2">Giới tính</label>
                <select name="Gender" class="form-select form-select-solid" data-control="select2">
                    <option value="">Chọn giới tính</option>
                    ${buildOptions(genders, 'id', 'name')}
                </select>
            </div>
            <!--end::Col-->
            <!--begin::Col-->
            <div class="col-xl-6 form-group fv-row">
                <label class="control-label fs-6 fw-bold mb-2">Số điện thoại</label>
                <input name="Phone" class="form-control form-control-solid mb-3 mb-lg-0" autocomplete="off"/>
            </div>
            <!--end::Col-->
        </div>
        <div class="row mb-3">
            <div class="col-lg-12 form-group fv-row">
                <label class="control-label fs-6 fw-bold mb-2">Ảnh đại diện</label>
                <input
                    type="file"
                    name="AvatarImage"
                    class="form-control form-control-solid mb-3"
                    onchange="fImageInput(this)"
                    data-preview-target="#avatar-preview"/>
                <div class="d-flex justify-content-center">
                    <img src="" alt="" id="avatar-preview" style="height: min-content; max-height: 200px;"/>
                </div>
            </div>
        </div>
        <div class="row mb-7">
            <div class="col-lg-12 form-group fv-row">
                <label class="control-label fs-6 fw-bold mb-2">Địa chỉ</label>
                <input name="Address" class="form-control form-control-solid mb-3 mb-lg-0" autocomplete="off"/>
            </div>
        </div>
        <div class="row mb-7">
            <div class="col-lg-12 form-group fv-row">
                <label class="control-label fs-6 fw-bold mb-2">Email</label>
                <input name="Email" class="form-control form-control-solid mb-3 mb-lg-0" placeholder="Email" autocomplete="off"/>
            </div>
        </div>
        <div class="row mb-7">
            <div class="col-lg-12 form-group">
                <label class="control-label fs-6 fw-bold mb-2">Mật khẩu</label>
                <input name="Password" class="form-control form-control-solid mb-3 mb-lg-0" type="password" placeholder="Mật khẩu" autocomplete="off"/>
            </div>
        </div>
        <div class="row mb-7">
            <!--begin::Col-->
            <div class="col-xl-6 form-group fv-row">
                <label class="control-label fs-6 fw-bold mb-2">Tỉnh/Thành phố<</label>
                <select name="ProvinceID" class="form-select form-select-solid" data-control="select2">
                    <option value="">Chọn Tỉnh/Thành phố</option>
                    ${buildOptions(data.provinces, 'id', 'name')}
                </select>
            </div>
            <!--end::Col-->
            <!--begin::Col-->
            <div class="col-xl-6 form-group fv-row">
                <label class="control-label fs-6 fw-bold mb-2">Loại người dùng</label>
                <select name="UserTypeID" class="form-select form-select-solid">
                    <option value="">Chọn loại người dùng</option>
                    ${buildOptions(data.userTypes, 'id', 'name')}
                </select>
            </div>
            <!--end::Col-->
        </div>
        <div class="row mb-7">
            <div class="col-lg-12 form-group fv-row">
                <label class="control-label fs-6 fw-bold mb-2">Nhóm người dùng</label>
                <select name="UserRoleID" class="form-select form-select-solid">
                    <option value="">Chọn nhóm quyền</option>
                    ${buildOptions(data.userRoles, 'id', 'name')}
                </select>
                <span asp-validation-for="UserRoleID" class="text-danger"></span>
            </div>
        </div>
        <div class="row mb-7 form-group">
            <label class="control-label fs-6 fw-bold mb-2">Thông tin khác</label>
            <div class="form-floating">
                <textarea name="About" class="form-control form-control-solid mb-3 mb-lg-0" rows="5"></textarea>
            </div>
        </div>
        <div class="row mb-7 form-group">
            <!--begin::Col-->
            <div class="col-xl-6 fv-row">
                <label class="control-label mb-4">Trạng thái</label>
                <div class="form-check form-switch form-check-custom form-check-solid">
                    <input name="Status" class="form-check-input" value="true" type="checkbox"/>
                    <label class="form-check-label"></label>
                </div>
            </div>
            <!--end::Col-->
            ${ data.currentUser.isRoot === true ?
                    `
                    <!--begin::Col-->
                    <div class="col-xl-6 ">
                        <label class="control-label mb-4">Cấp độ tài khoản</label>
                        <div class="form-check form-switch form-check-custom form-check-solid">
                            <input name="IsRoot" class="form-check-input" value="true" type="checkbox"/>
                            <label class="form-check-label"></label>
                        </div>
                    </div>
                    <!--end::Col-->`
                : ""
            }
        </div>
        <div class="row">
            <div class="text-center pt-15">
                <button type="reset" class="btn btn-light me-3 remove-form" data-bs-dismiss="modal">Hủy</button>
                <button type="submit" class="btn btn-primary" id="submit-form-create-user">Lưu</button>
            </div>
        </div>
    `;
};

